mapper.insert:
INSERT INTO BUSINESSTRIP
	( BT_ID
	  , AUTHOR_ID
	  , AUTHOR_DEPT
	  , APPROVER_ID
	  , RECEIVER_ID
	  , TRAVELER_ID
	  , TITLE
	  , LOCATION
	  , TRIP_START_DATE
	  , TRIP_END_DATE
	  , TRANSPORTATION_TYPE
	  , TRIP_PURPOSE
	  , FILENAME
	  , APPROVAL_STATE
	  , CREATED_AT )
VALUES ( #{BT_ID}
	  , #{AUTHOR_ID}
	  , #{AUTHOR_DEPT}
	  , #{APPROVER_ID}
	  , #{RECEIVER_ID}
	  , #{TRAVELER_ID}
	  , #{TITLE}
	  , #{LOCATION}
	  , #{TRIP_START_DATE}
	  , #{TRIP_END_DATE}
	  , #{TRANSPORTATION_TYPE}
	  , #{TRIP_PURPOSE}
	  , #{FILENAME}
	  , 0
	  , NOW())

INSERT INTO BUSINESSTRIP_ROLES
	( BT_ID
	  , USER_ID
	  , USER_NAME
	  , USER_DEPT
	  , USER_POSITION
	  , USER_TYPE )
VALUES ( #{BT_ID}
	  , #{USER_ID}
	  , #{USER_NAME}
	  , #{USER_DEPT}
	  , #{USER_POSITION}
	  , #{USER_TYPE} )

INSERT INTO BUSINESSTRIP_EXPENSES
	( BT_ID
	  , EXPENSE_ID
	  , EXPENSE_TYPE
	  , EXPENSE_DETAIL
	  , PAYMENT_METHOD
	  , PRICE )
VALUES ( #{BT_ID}
	  , #{EXPENSE_ID}
	  , #{EXPENSE_TYPE}
	  , #{EXPENSE_DETAIL}
	  , #{PAYMENT_METHOD}
	  , #{PRICE} )

INSERT INTO COMPANY_EXPENSES
	( EXPENSE_ID
	  , EXPENSE_DATE
	  , EXPENSE_DETAIL
	  , EXPENSE_TYPE
	  , PAYMENT_METHOD
	  , PRICE )
VALUES ( #{EXPENSE_ID}
	  , #{EXPENSE_DATE}
	  , #{EXPENSE_DETAIL}
	  , #{EXPENSE_TYPE}
	  , #{PAYMENT_METHOD}
	  , #{PRICE} )

INSERT INTO BUSINESSTRIP_FILES
	( BT_ID
	  , ORIGIN_FILE_NAME
	  , FILENAME
	  , UPLOADED_AT )
VALUES ( #{BT_ID}
	  , #{ORIGIN_FILE_NAME}
	  , #{FILENAME}
	  , #{UPLOADED_AT})

mapper.update:
UPDATE BUSINESSTRIP
SET 
	 AUTHOR_ID=#{AUTHOR_ID}
	, AUTHOR_DEPT=#{AUTHOR_DEPT}
	, APPROVER_ID=#{APPROVER_ID}
	, RECEIVER_ID=#{RECEIVER_ID}
	, TRAVELER_ID=#{TRAVELER_ID}
	, TITLE=#{TITLE}
	, LOCATION=#{LOCATION}
	, TRIP_START_DATE=#{TRIP_START_DATE}
	, TRIP_END_DATE=#{TRIP_END_DATE}
	, TRANSPORTATION_TYPE=#{TRANSPORTATION_TYPE}
	, TRIP_PURPOSE=#{TRIP_PURPOSE}
	, FILENAME=#{FILENAME}
WHERE BT_ID=#{BT_ID}

UPDATE BUSINESSTRIP_ROLES
SET BT_ID=#{BT_ID}
	, USER_ID=#{USER_ID}
	, USER_NAME=#{USER_NAME}
	, USER_DEPT=#{USER_DEPT}
	, USER_POSITION=#{USER_POSITION}
	, USER_TYPE=#{USER_TYPE}
WHERE BT_ROLE_ID=#{BT_ROLE_ID}

UPDATE BUSINESSTRIP_EXPENSES
SET 
	EXPENSE_ID=#{EXPENSE_ID}
	, EXPENSE_TYPE=#{EXPENSE_TYPE}
	, EXPENSE_DETAIL=#{EXPENSE_DETAIL}
	, PAYMENT_METHOD=#{PAYMENT_METHOD}
	, PRICE=#{PRICE}
WHERE BT_EXP_ID=#{BT_EXP_ID}

UPDATE COMPANY_EXPENSES
SET 
	EXPENSE_DATE=#{EXPENSE_DATE}
	, EXPENSE_DETAIL=#{EXPENSE_DETAIL}
	, EXPENSE_TYPE=#{EXPENSE_TYPE}
	, PAYMENT_METHOD=#{PAYMENT_METHOD}
	, PRICE=#{PRICE}
WHERE EXPENSE_ID=#{EXPENSE_ID}
